document.addEventListener("DOMContentLoaded",(function(){var e=document;e.querySelector(".filter-area").addEventListener("click",(function(){this.classList.contains("active")?this.classList.remove("active"):this.classList.add("active")})),e.querySelector(".btn-ask-filter").addEventListener("click",(function(t){t.preventDefault(),e.querySelector(".filter").classList.add("active")})),e.querySelector(".close").addEventListener("click",(function(t){t.preventDefault(),e.querySelector(".filter").classList.remove("active")}))}));